import React from 'react';
import { Link as RouterLink } from 'react-router-dom';
import {
  Card,
  CardActionArea,
  CardContent,
  CardMedia,
  Typography,
  Box,
  Chip,
  Rating,
  Tooltip,
} from '@mui/material';
import { PlayArrow, Visibility } from '@mui/icons-material';

const MovieCard = ({ movie }) => {
  // Xử lý khi không có poster
  const posterUrl = movie.poster_url && movie.poster_url.startsWith('http')
    ? movie.poster_url
    : movie.poster_url
      ? `https://phimimg.com/${movie.poster_url}`
      : 'https://via.placeholder.com/300x450?text=No+Image';

  // Xử lý rating từ TMDB nếu có
  const rating = movie.tmdb && movie.tmdb.vote_average ? movie.tmdb.vote_average / 2 : 0;

  return (
    <Card
      sx={{
        height: '100%',
        display: 'flex',
        flexDirection: 'column',
        position: 'relative',
        overflow: 'hidden',
        borderRadius: { xs: 1, sm: 2 },
        boxShadow: { xs: 2, sm: 3 },
        '&:hover .movie-overlay': {
          opacity: 1,
        },
        '&:hover .movie-play-icon': {
          transform: 'translate(-50%, -50%) scale(1.2)',
        },
      }}
    >
      <CardActionArea component={RouterLink} to={`/phim/${movie.slug}`} sx={{ flexGrow: 1 }}>
        <Box sx={{ 
          position: 'relative', 
          paddingTop: { xs: '140%', sm: '150%' } /* Responsive aspect ratio */
        }}>
          <CardMedia
            component="img"
            image={posterUrl}
            alt={movie.name}
            sx={{
              position: 'absolute',
              top: 0,
              left: 0,
              width: '100%',
              height: '100%',
              objectFit: 'cover',
              transition: 'transform 0.3s ease',
            }}
          />

          {/* Overlay with play button */}
          <Box
            className="movie-overlay"
            sx={{
              position: 'absolute',
              top: 0,
              left: 0,
              width: '100%',
              height: '100%',
              bgcolor: 'rgba(0, 0, 0, 0.5)',
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center',
              opacity: 0,
              transition: 'opacity 0.3s ease',
            }}
          >
            <Box
              className="movie-play-icon"
              sx={{
                position: 'absolute',
                top: '50%',
                left: '50%',
                transform: 'translate(-50%, -50%)',
                width: 60,
                height: 60,
                borderRadius: '50%',
                bgcolor: 'primary.main',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                transition: 'transform 0.3s ease',
              }}
            >
              <PlayArrow sx={{ fontSize: 40, color: 'white' }} />
            </Box>
          </Box>

          {/* Episode badge if it's a series */}
          {movie.episode_current && (
            <Chip
              label={movie.episode_current}
              color="primary"
              size="small"
              sx={{
                position: 'absolute',
                top: 10,
                right: 10,
                fontWeight: 'bold',
                fontSize: '0.75rem',
              }}
            />
          )}

          {/* Quality badge */}
          {movie.quality && (
            <Chip
              label={movie.quality}
              color="secondary"
              size="small"
              sx={{
                position: 'absolute',
                top: 10,
                left: 10,
                fontWeight: 'bold',
                fontSize: '0.75rem',
              }}
            />
          )}

          {/* Year badge */}
          {movie.year && (
            <Chip
              label={movie.year}
              color="info"
              size="small"
              sx={{
                position: 'absolute',
                bottom: 10,
                right: 10,
                fontWeight: 'bold',
                fontSize: '0.75rem',
              }}
            />
          )}
        </Box>

        <CardContent sx={{ 
          flexGrow: 1, 
          p: { xs: 1, sm: 2 },
          '&:last-child': { pb: { xs: 1, sm: 2 } }
        }}>
          <Tooltip title={movie.name} placement="top">
            <Typography
              gutterBottom
              variant="h6"
              component="h3"
              sx={{
                fontWeight: 'bold',
                fontSize: { xs: '0.9rem', sm: '1.1rem' },
                lineHeight: 1.2,
                display: '-webkit-box',
                WebkitLineClamp: 2,
                WebkitBoxOrient: 'vertical',
                overflow: 'hidden',
                textOverflow: 'ellipsis',
                minHeight: { xs: '2.2em', sm: '2.4em' },
                mb: { xs: 0.5, sm: 1 },
              }}
            >
              {movie.name}
            </Typography>
          </Tooltip>

          {movie.origin_name && movie.origin_name !== movie.name && (
            <Typography
              variant="body2"
              color="text.secondary"
              sx={{
                fontStyle: 'italic',
                fontSize: { xs: '0.75rem', sm: '0.875rem' },
                mb: { xs: 0.5, sm: 1 },
                display: '-webkit-box',
                WebkitLineClamp: 1,
                WebkitBoxOrient: 'vertical',
                overflow: 'hidden',
                textOverflow: 'ellipsis',
              }}
            >
              {movie.origin_name}
            </Typography>
          )}

          {/* Rating */}
          {rating > 0 && (
            <Box sx={{ 
              display: 'flex', 
              alignItems: 'center', 
              gap: { xs: 0.5, sm: 1 }, 
              mb: { xs: 0.5, sm: 1 },
              flexWrap: 'wrap'
            }}>
              <Rating
                value={rating}
                precision={0.5}
                size="small"
                readOnly
                sx={{ mr: 1 }}
              />
              <Typography variant="body2" color="text.secondary">
                {rating.toFixed(1)}/5
              </Typography>
            </Box>
          )}
        </CardContent>
      </CardActionArea>
    </Card>
  );
};

export default MovieCard;
