import React, { useState } from 'react';
import { Link as RouterLink } from 'react-router-dom';
import Slider from 'react-slick';
import {
  Box,
  Typography,
  Button,
  Rating,
  useTheme,
  useMediaQuery,
  Paper,
  Chip,
} from '@mui/material';
import { PlayArrow, Info } from '@mui/icons-material';
import 'slick-carousel/slick/slick.css';
import 'slick-carousel/slick/slick-theme.css';

const HeroSlider = ({ movies }) => {
  const [currentSlide, setCurrentSlide] = useState(0);
  const theme = useTheme();
  const isMobile = useMediaQuery(theme.breakpoints.down('sm'));

  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
    autoplay: true,
    autoplaySpeed: 6000,
    pauseOnHover: true,
    beforeChange: (current, next) => setCurrentSlide(next),
    dotsClass: 'slick-dots custom-dots',
    customPaging: (i) => (
      <Box
        sx={{
          width: 12,
          height: 12,
          borderRadius: '50%',
          backgroundColor: i === currentSlide ? 'primary.main' : 'rgba(255, 255, 255, 0.5)',
          transition: 'all 0.3s ease',
          cursor: 'pointer',
        }}
      />
    ),
  };

  if (!movies || movies.length === 0) {
    return null;
  }

  return (
    <Box
      sx={{
        position: 'relative',
        mb: 6,
        '.slick-dots': {
          bottom: 20,
        },
        '.slick-prev, .slick-next': {
          zIndex: 1,
          width: 40,
          height: 40,
          backgroundColor: 'rgba(0, 0, 0, 0.5)',
          borderRadius: '50%',
          transition: 'all 0.3s ease',
          '&:hover': {
            backgroundColor: 'primary.main',
          },
          '&::before': {
            fontSize: 20,
          },
        },
        '.slick-prev': {
          left: 20,
        },
        '.slick-next': {
          right: 20,
        },
      }}
    >
      <Slider {...settings}>
        {movies.map((movie) => {
          // Xử lý khi không có poster
          const backdropUrl = movie.thumb_url && movie.thumb_url.startsWith('http')
            ? movie.thumb_url
            : movie.thumb_url
              ? `https://phimimg.com/${movie.thumb_url}`
              : 'https://via.placeholder.com/1200x500?text=No+Image';

          // Xử lý rating từ TMDB nếu có
          const rating = movie.tmdb && movie.tmdb.vote_average ? movie.tmdb.vote_average / 2 : 0;

          return (
            <Box key={movie._id} sx={{ position: 'relative' }}>
              {/* Backdrop image */}
              <Box
                sx={{
                  position: 'relative',
                  height: { xs: '50vh', sm: '60vh', md: '70vh' },
                  maxHeight: 700,
                  overflow: 'hidden',
                }}
              >
                <Box
                  component="img"
                  src={backdropUrl}
                  alt={movie.name}
                  sx={{
                    width: '100%',
                    height: '100%',
                    objectFit: 'cover',
                    filter: 'brightness(0.7)',
                  }}
                />

                {/* Gradient overlay */}
                <Box
                  sx={{
                    position: 'absolute',
                    top: 0,
                    left: 0,
                    width: '100%',
                    height: '100%',
                    background: 'linear-gradient(to top, rgba(0,0,0,0.9) 0%, rgba(0,0,0,0.5) 50%, rgba(0,0,0,0.3) 100%)',
                  }}
                />

                {/* Content */}
                <Box
                  sx={{
                    position: 'absolute',
                    bottom: 0,
                    left: 0,
                    width: '100%',
                    p: { xs: 3, sm: 5, md: 8 },
                    color: 'white',
                    display: 'flex',
                    flexDirection: { xs: 'column', md: 'row' },
                    alignItems: { xs: 'flex-start', md: 'center' },
                    gap: 4,
                  }}
                >
                  {/* Poster */}
                  <Box
                    sx={{
                      display: { xs: 'none', sm: 'block' },
                      width: { sm: '150px', md: '200px' },
                      height: { sm: '225px', md: '300px' },
                      borderRadius: 2,
                      overflow: 'hidden',
                      boxShadow: '0 5px 15px rgba(0,0,0,0.5)',
                    }}
                  >
                    <Box
                      component="img"
                      src={
                        movie.poster_url && movie.poster_url.startsWith('http')
                          ? movie.poster_url
                          : movie.poster_url
                            ? `https://phimimg.com/${movie.poster_url}`
                            : 'https://via.placeholder.com/300x450?text=No+Image'
                      }
                      alt={movie.name}
                      sx={{
                        width: '100%',
                        height: '100%',
                        objectFit: 'cover',
                      }}
                    />
                  </Box>

                  {/* Info */}
                  <Box sx={{ maxWidth: { xs: '100%', md: '60%' } }}>
                    <Typography
                      variant="h3"
                      component="h1"
                      sx={{
                        fontWeight: 'bold',
                        mb: 1,
                        fontSize: { xs: '1.75rem', sm: '2.5rem', md: '3rem' },
                        textShadow: '2px 2px 4px rgba(0,0,0,0.5)',
                      }}
                    >
                      {movie.name}
                    </Typography>

                    {movie.origin_name && (
                      <Typography
                        variant="h6"
                        sx={{
                          mb: 2,
                          opacity: 0.8,
                          fontStyle: 'italic',
                          textShadow: '1px 1px 2px rgba(0,0,0,0.5)',
                        }}
                      >
                        {movie.origin_name}
                      </Typography>
                    )}

                    <Box sx={{ display: 'flex', flexWrap: 'wrap', gap: 1, mb: 2 }}>
                      {movie.year && (
                        <Chip
                          label={movie.year}
                          size="small"
                          color="primary"
                          sx={{ fontWeight: 'bold' }}
                        />
                      )}
                      {movie.quality && (
                        <Chip
                          label={movie.quality}
                          size="small"
                          color="secondary"
                          sx={{ fontWeight: 'bold' }}
                        />
                      )}
                      {movie.episode_current && (
                        <Chip
                          label={movie.episode_current}
                          size="small"
                          color="info"
                          sx={{ fontWeight: 'bold' }}
                        />
                      )}
                      {movie.lang && (
                        <Chip
                          label={movie.lang}
                          size="small"
                          color="default"
                          sx={{ fontWeight: 'bold' }}
                        />
                      )}
                    </Box>

                    {/* Rating */}
                    {rating > 0 && (
                      <Box sx={{ display: 'flex', alignItems: 'center', mb: 2 }}>
                        <Rating
                          value={rating}
                          precision={0.5}
                          readOnly
                          sx={{ mr: 1 }}
                        />
                        <Typography variant="body2">
                          {rating.toFixed(1)}/5
                        </Typography>
                      </Box>
                    )}

                    {/* Description */}
                    {movie.content && !isMobile && (
                      <Typography
                        variant="body1"
                        sx={{
                          mb: 3,
                          display: '-webkit-box',
                          WebkitLineClamp: 3,
                          WebkitBoxOrient: 'vertical',
                          overflow: 'hidden',
                          textOverflow: 'ellipsis',
                          textShadow: '1px 1px 1px rgba(0,0,0,0.5)',
                        }}
                      >
                        {movie.content}
                      </Typography>
                    )}

                    {/* Buttons */}
                    <Box sx={{ display: 'flex', gap: 2 }}>
                      <Button
                        component={RouterLink}
                        to={`/phim/${movie.slug}`}
                        variant="contained"
                        color="primary"
                        size="large"
                        startIcon={<PlayArrow />}
                        sx={{
                          borderRadius: 8,
                          px: 3,
                          py: 1,
                          fontWeight: 'bold',
                        }}
                      >
                        Xem Phim
                      </Button>
                      <Button
                        component={RouterLink}
                        to={`/phim/${movie.slug}`}
                        variant="outlined"
                        color="inherit"
                        size="large"
                        startIcon={<Info />}
                        sx={{
                          borderRadius: 8,
                          px: 3,
                          py: 1,
                          borderColor: 'white',
                          '&:hover': {
                            borderColor: 'white',
                            backgroundColor: 'rgba(255, 255, 255, 0.1)',
                          },
                        }}
                      >
                        Chi Tiết
                      </Button>
                    </Box>
                  </Box>
                </Box>
              </Box>
            </Box>
          );
        })}
      </Slider>
    </Box>
  );
};

export default HeroSlider;
