import React from 'react';
import { Link as RouterLink } from 'react-router-dom';
import {
  Box,
  Container,
  Grid,
  Typography,
  Link,
  Divider,
  IconButton,
  useMediaQuery,
} from '@mui/material';
import FacebookIcon from '@mui/icons-material/Facebook';
import TwitterIcon from '@mui/icons-material/Twitter';
import InstagramIcon from '@mui/icons-material/Instagram';
import YouTubeIcon from '@mui/icons-material/YouTube';
import MovieIcon from '@mui/icons-material/Movie';

import { useAppContext } from '../context/AppContext';

const Footer = () => {
  const { categories, countries } = useAppContext();
  const isMobile = useMediaQuery((theme) => theme.breakpoints.down('sm'));

  // Tạo danh sách năm từ năm hiện tại đến 5 năm trước
  const currentYear = new Date().getFullYear();
  const years = Array.from({ length: 6 }, (_, i) => currentYear - i);

  return (
    <Box
      component="footer"
      sx={{
        bgcolor: 'background.paper',
        py: 6,
        mt: 'auto',
      }}
    >
      <Container maxWidth="lg">
        <Grid container spacing={4}>
          {/* Logo and description */}
          <Grid item xs={12} md={4}>
            <Box sx={{ display: 'flex', alignItems: 'center', mb: 2 }}>
              <MovieIcon sx={{ mr: 1, color: 'primary.main' }} />
              <Typography
                variant="h6"
                component={RouterLink}
                to="/"
                sx={{
                  fontWeight: 700,
                  letterSpacing: '.1rem',
                  color: 'primary.main',
                  textDecoration: 'none',
                }}
              >
                PHIM HAY
              </Typography>
            </Box>
            <Typography variant="body2" color="text.secondary" paragraph>
              Trang web xem phim trực tuyến với kho phim đa dạng, phong phú. Cập nhật nhanh chóng các phim mới, phim hot với chất lượng cao và hoàn toàn miễn phí.
            </Typography>
            <Box sx={{ mt: 2 }}>
              <IconButton color="primary" aria-label="facebook">
                <FacebookIcon />
              </IconButton>
              <IconButton color="primary" aria-label="twitter">
                <TwitterIcon />
              </IconButton>
              <IconButton color="primary" aria-label="instagram">
                <InstagramIcon />
              </IconButton>
              <IconButton color="primary" aria-label="youtube">
                <YouTubeIcon />
              </IconButton>
            </Box>
          </Grid>

          {/* Thể loại */}
          <Grid item xs={6} md={2}>
            <Typography variant="h6" color="text.primary" gutterBottom>
              Thể loại
            </Typography>
            {categories.slice(0, 10).map((category) => (
              <Link
                key={category.slug}
                component={RouterLink}
                to={`/the-loai/${category.slug}`}
                color="text.secondary"
                display="block"
                sx={{ mb: 1, '&:hover': { color: 'primary.main' } }}
                underline="none"
              >
                {category.name}
              </Link>
            ))}
          </Grid>

          {/* Quốc gia */}
          <Grid item xs={6} md={2}>
            <Typography variant="h6" color="text.primary" gutterBottom>
              Quốc gia
            </Typography>
            {countries.slice(0, 10).map((country) => (
              <Link
                key={country.slug}
                component={RouterLink}
                to={`/quoc-gia/${country.slug}`}
                color="text.secondary"
                display="block"
                sx={{ mb: 1, '&:hover': { color: 'primary.main' } }}
                underline="none"
              >
                {country.name}
              </Link>
            ))}
          </Grid>

          {/* Years and Links */}
          <Grid item xs={12} md={4}>
            <Grid container spacing={2}>
              <Grid item xs={6}>
                <Typography variant="h6" color="text.primary" gutterBottom>
                  Năm
                </Typography>
                {years.map((year) => (
                  <Link
                    key={year}
                    component={RouterLink}
                    to={`/nam/${year}`}
                    color="text.secondary"
                    display="block"
                    sx={{ mb: 1, '&:hover': { color: 'primary.main' } }}
                    underline="none"
                  >
                    Phim {year}
                  </Link>
                ))}
              </Grid>
              <Grid item xs={6}>
                <Typography variant="h6" color="text.primary" gutterBottom>
                  Liên Kết
                </Typography>
                <Link
                  component={RouterLink}
                  to="/danh-sach/phim-le"
                  color="text.secondary"
                  display="block"
                  sx={{ mb: 1, '&:hover': { color: 'primary.main' } }}
                  underline="none"
                >
                  Phim Lẻ
                </Link>
                <Link
                  component={RouterLink}
                  to="/danh-sach/phim-bo"
                  color="text.secondary"
                  display="block"
                  sx={{ mb: 1, '&:hover': { color: 'primary.main' } }}
                  underline="none"
                >
                  Phim Bộ
                </Link>
                <Link
                  component={RouterLink}
                  to="/danh-sach/phim-chieu-rap"
                  color="text.secondary"
                  display="block"
                  sx={{ mb: 1, '&:hover': { color: 'primary.main' } }}
                  underline="none"
                >
                  Phim Chiếu Rạp
                </Link>
                <Link
                  component={RouterLink}
                  to="/danh-sach/phim-moi"
                  color="text.secondary"
                  display="block"
                  sx={{ mb: 1, '&:hover': { color: 'primary.main' } }}
                  underline="none"
                >
                  Phim Mới
                </Link>
              </Grid>
            </Grid>
          </Grid>
        </Grid>

        <Divider sx={{ my: 3 }} />

        <Box sx={{ display: 'flex', flexDirection: isMobile ? 'column' : 'row', justifyContent: 'space-between', alignItems: isMobile ? 'center' : 'flex-start' }}>
          <Typography variant="body2" color="text.secondary" align={isMobile ? 'center' : 'left'}>
            © {currentYear} PHIM HAY. Tất cả quyền được bảo lưu.
          </Typography>
          <Box sx={{ display: 'flex', mt: isMobile ? 2 : 0 }}>
            <Link href="#" color="text.secondary" sx={{ mx: 1 }} underline="none">
              Điều khoản sử dụng
            </Link>
            <Link href="#" color="text.secondary" sx={{ mx: 1 }} underline="none">
              Chính sách bảo mật
            </Link>
            <Link href="#" color="text.secondary" sx={{ mx: 1 }} underline="none">
              Liên hệ
            </Link>
          </Box>
        </Box>
      </Container>
    </Box>
  );
};

export default Footer;
