import React from 'react';
import Slider from 'react-slick';
import { Box, Typography, useTheme, useMediaQuery } from '@mui/material';
import MovieCard from './MovieCard';
import 'slick-carousel/slick/slick.css';
import 'slick-carousel/slick/slick-theme.css';

const MovieSlider = ({ title, movies, slidesToShow = 6 }) => {
  const theme = useTheme();
  const isXs = useMediaQuery(theme.breakpoints.down('sm'));
  const isSm = useMediaQuery(theme.breakpoints.between('sm', 'md'));
  const isMd = useMediaQuery(theme.breakpoints.between('md', 'lg'));
  const isLg = useMediaQuery(theme.breakpoints.between('lg', 'xl'));

  // Responsive settings
  const getResponsiveSlidesToShow = () => {
    if (isXs) return Math.min(2, movies.length);
    if (isSm) return Math.min(3, movies.length);
    if (isMd) return Math.min(4, movies.length);
    if (isLg) return Math.min(5, movies.length);
    return Math.min(slidesToShow, movies.length);
  };

  const settings = {
    dots: false,
    infinite: movies.length > getResponsiveSlidesToShow(),
    speed: 500,
    slidesToShow: getResponsiveSlidesToShow(),
    slidesToScroll: 1,
    autoplay: movies.length > getResponsiveSlidesToShow(),
    autoplaySpeed: 4000,
    pauseOnHover: true,
    swipeToSlide: true,
    arrows: !isXs && movies.length > getResponsiveSlidesToShow(),
    touchMove: true,
    draggable: true,
    responsive: [
      {
        breakpoint: 1536, // xl
        settings: {
          slidesToShow: Math.min(6, movies.length),
          slidesToScroll: 1,
          arrows: movies.length > 6,
        },
      },
      {
        breakpoint: 1200, // lg
        settings: {
          slidesToShow: Math.min(5, movies.length),
          slidesToScroll: 1,
          arrows: movies.length > 5,
        },
      },
      {
        breakpoint: 900, // md
        settings: {
          slidesToShow: Math.min(4, movies.length),
          slidesToScroll: 1,
          arrows: movies.length > 4,
        },
      },
      {
        breakpoint: 600, // sm
        settings: {
          slidesToShow: Math.min(3, movies.length),
          slidesToScroll: 1,
          arrows: false,
        },
      },
      {
        breakpoint: 480, // xs
        settings: {
          slidesToShow: Math.min(2, movies.length),
          slidesToScroll: 1,
          arrows: false,
          centerMode: movies.length === 1,
          centerPadding: movies.length === 1 ? '20%' : '0',
        },
      },
    ],
  };

  if (!movies || movies.length === 0) {
    return null;
  }

  return (
    <Box sx={{ mb: 6 }}>
      {title && (
        <Box sx={{ 
          display: 'flex', 
          justifyContent: 'space-between', 
          alignItems: 'center', 
          mb: 4 
        }}>
          <Typography 
            variant="h5" 
            component="h2" 
            sx={{ 
              fontWeight: 'bold',
              color: 'text.primary',
              position: 'relative',
              '&::after': {
                content: '""',
                position: 'absolute',
                bottom: -8,
                left: 0,
                width: '60px',
                height: '3px',
                background: 'linear-gradient(45deg, #ff6b6b, #4ecdc4)',
                borderRadius: '2px'
              }
            }}
          >
            {title}
          </Typography>
          {viewMoreLink && (
            <Button
              component={Link}
              to={viewMoreLink}
              variant="contained"
              size="small"
              sx={{
                background: 'linear-gradient(45deg, #667eea 30%, #764ba2 90%)',
                borderRadius: 3,
                textTransform: 'none',
                fontWeight: 'bold',
                px: 3,
                py: 1,
                '&:hover': {
                  background: 'linear-gradient(45deg, #764ba2 30%, #667eea 90%)',
                  transform: 'translateY(-1px)',
                  boxShadow: '0 4px 12px rgba(0,0,0,0.3)'
                },
                transition: 'all 0.3s ease'
              }}
            >
              Xem thêm →
            </Button>
          )}
        </Box>
      )}

      <Box sx={{
        '.slick-track': {
          display: 'flex',
          alignItems: 'stretch'
        },
        '.slick-slide': {
          '& > div': {
            height: '100%'
          }
        }
      }}>
        <Slider {...settings}>
          {movies.map((movie) => (
            <Box key={movie._id} sx={{ px: 1, height: '100%' }}>
              <MovieCard movie={movie} />
            </Box>
          ))}
        </Slider>
      </Box>
    </Box>
  );
};

export default MovieSlider;
