import React, { useState, useEffect } from 'react';
import { Link as RouterLink, useNavigate, useLocation } from 'react-router-dom';
import {
  AppBar,
  Box,
  Toolbar,
  IconButton,
  Typography,
  Menu,
  Container,
  Button,
  MenuItem,
  Drawer,
  List,
  ListItem,
  ListItemText,
  ListItemButton,
  Divider,
  useMediaQuery,
  useScrollTrigger,
  Slide,
} from '@mui/material';
import MenuIcon from '@mui/icons-material/Menu';
import SearchIcon from '@mui/icons-material/Search';
import CloseIcon from '@mui/icons-material/Close';
import MovieIcon from '@mui/icons-material/Movie';

import { useAppContext } from '../context/AppContext';
import SearchBar from './SearchBar';
import { MOVIE_TYPES } from '../constants/constants';

// Component để ẩn/hiện header khi cuộn
function HideOnScroll(props) {
  const { children } = props;
  const trigger = useScrollTrigger();

  return (
    <Slide appear={false} direction="down" in={!trigger}>
      {children}
    </Slide>
  );
}

const Header = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const { categories, countries } = useAppContext();
  const isMobile = useMediaQuery((theme) => theme.breakpoints.down('md'));

  const [mobileMenuOpen, setMobileMenuOpen] = useState(false);
  const [searchOpen, setSearchOpen] = useState(false);
  const [categoryMenu, setCategoryMenu] = useState(null);
  const [countryMenu, setCountryMenu] = useState(null);
  const [yearMenu, setYearMenu] = useState(null);
  const [typeMenu, setTypeMenu] = useState(null);

  // Tạo danh sách năm từ năm hiện tại đến 20 năm trước
  const currentYear = new Date().getFullYear();
  const years = Array.from({ length: 21 }, (_, i) => currentYear - i);

  // Xử lý đóng/mở menu mobile
  const handleMobileMenuToggle = () => {
    setMobileMenuOpen(!mobileMenuOpen);
  };

  // Xử lý đóng/mở thanh tìm kiếm
  const handleSearchToggle = () => {
    setSearchOpen(!searchOpen);
  };

  // Xử lý đóng menu khi chọn một mục
  const handleMenuItemClick = (path) => {
    navigate(path);
    setMobileMenuOpen(false);
    setCategoryMenu(null);
    setCountryMenu(null);
    setYearMenu(null);
    setTypeMenu(null);
  };

  // Xử lý menu thể loại
  const handleCategoryMenuOpen = (event) => {
    setCategoryMenu(event.currentTarget);
  };

  const handleCategoryMenuClose = () => {
    setCategoryMenu(null);
  };

  // Xử lý menu quốc gia
  const handleCountryMenuOpen = (event) => {
    setCountryMenu(event.currentTarget);
  };

  const handleCountryMenuClose = () => {
    setCountryMenu(null);
  };

  // Xử lý menu năm
  const handleYearMenuOpen = (event) => {
    setYearMenu(event.currentTarget);
  };

  const handleYearMenuClose = () => {
    setYearMenu(null);
  };

  // Xử lý menu loại phim
  const handleTypeMenuOpen = (event) => {
    setTypeMenu(event.currentTarget);
  };

  const handleTypeMenuClose = () => {
    setTypeMenu(null);
  };

  return (
    <>
      <HideOnScroll>
        <AppBar position="sticky" sx={{ bgcolor: 'background.header' }}>
          <Container maxWidth="xl">
            <Toolbar disableGutters>
              {/* Logo - Desktop */}
              <Typography
                variant="h6"
                noWrap
                component={RouterLink}
                to="/"
                sx={{
                  mr: 2,
                  display: { xs: 'none', md: 'flex' },
                  fontWeight: 700,
                  letterSpacing: '.1rem',
                  color: 'inherit',
                  textDecoration: 'none',
                  alignItems: 'center',
                }}
              >
                <MovieIcon sx={{ mr: 1 }} />
                PHIM HAY
              </Typography>

              {/* Menu Mobile Toggle */}
              <Box sx={{ flexGrow: 0, display: { xs: 'flex', md: 'none' } }}>
                <IconButton
                  size="large"
                  aria-label="menu"
                  aria-controls="menu-mobile"
                  aria-haspopup="true"
                  onClick={handleMobileMenuToggle}
                  color="inherit"
                >
                  <MenuIcon />
                </IconButton>
              </Box>

              {/* Logo - Mobile */}
              <Typography
                variant="h6"
                noWrap
                component={RouterLink}
                to="/"
                sx={{
                  flexGrow: 1,
                  display: { xs: 'flex', md: 'none' },
                  fontWeight: 700,
                  letterSpacing: '.1rem',
                  color: 'inherit',
                  textDecoration: 'none',
                  alignItems: 'center',
                }}
              >
                <MovieIcon sx={{ mr: 1 }} />
                PHIM HAY
              </Typography>

              {/* Desktop Navigation */}
              <Box sx={{ flexGrow: 1, display: { xs: 'none', md: 'flex' } }}>
                {/* Thể loại */}
                <Button
                  onClick={handleCategoryMenuOpen}
                  sx={{ my: 2, color: 'white', display: 'block' }}
                >
                  Thể loại
                </Button>
                <Menu
                  id="category-menu"
                  anchorEl={categoryMenu}
                  open={Boolean(categoryMenu)}
                  onClose={handleCategoryMenuClose}
                  MenuListProps={{
                    'aria-labelledby': 'category-button',
                  }}
                  PaperProps={{
                    style: {
                      maxHeight: 400,
                      width: '20ch',
                    },
                  }}
                >
                  {categories.map((category) => (
                    <MenuItem
                      key={category.slug}
                      onClick={() => handleMenuItemClick(`/the-loai/${category.slug}`)}
                    >
                      {category.name}
                    </MenuItem>
                  ))}
                </Menu>

                {/* Quốc gia */}
                <Button
                  onClick={handleCountryMenuOpen}
                  sx={{ my: 2, color: 'white', display: 'block' }}
                >
                  Quốc gia
                </Button>
                <Menu
                  id="country-menu"
                  anchorEl={countryMenu}
                  open={Boolean(countryMenu)}
                  onClose={handleCountryMenuClose}
                  MenuListProps={{
                    'aria-labelledby': 'country-button',
                  }}
                  PaperProps={{
                    style: {
                      maxHeight: 400,
                      width: '20ch',
                    },
                  }}
                >
                  {countries.map((country) => (
                    <MenuItem
                      key={country.slug}
                      onClick={() => handleMenuItemClick(`/quoc-gia/${country.slug}`)}
                    >
                      {country.name}
                    </MenuItem>
                  ))}
                </Menu>

                {/* Năm */}
                <Button
                  onClick={handleYearMenuOpen}
                  sx={{ my: 2, color: 'white', display: 'block' }}
                >
                  Năm
                </Button>
                <Menu
                  id="year-menu"
                  anchorEl={yearMenu}
                  open={Boolean(yearMenu)}
                  onClose={handleYearMenuClose}
                  MenuListProps={{
                    'aria-labelledby': 'year-button',
                  }}
                  PaperProps={{
                    style: {
                      maxHeight: 400,
                      width: '15ch',
                    },
                  }}
                >
                  {years.map((year) => (
                    <MenuItem
                      key={year}
                      onClick={() => handleMenuItemClick(`/nam/${year}`)}
                    >
                      {year}
                    </MenuItem>
                  ))}
                </Menu>

                {/* Loại phim */}
                <Button
                  onClick={handleTypeMenuOpen}
                  sx={{ my: 2, color: 'white', display: 'block' }}
                >
                  Loại phim
                </Button>
                <Menu
                  id="type-menu"
                  anchorEl={typeMenu}
                  open={Boolean(typeMenu)}
                  onClose={handleTypeMenuClose}
                  MenuListProps={{
                    'aria-labelledby': 'type-button',
                  }}
                  PaperProps={{
                    style: {
                      maxHeight: 400,
                      width: '25ch',
                    },
                  }}
                >
                  {MOVIE_TYPES.map((type) => (
                    <MenuItem
                      key={type.slug}
                      onClick={() => handleMenuItemClick(`/danh-sach/${type.slug}`)}
                    >
                      {type.name}
                    </MenuItem>
                  ))}
                </Menu>
              </Box>

              {/* Search Icon */}
              <Box sx={{ flexGrow: 0 }}>
                <IconButton
                  size="large"
                  aria-label="search"
                  color="inherit"
                  onClick={handleSearchToggle}
                >
                  {searchOpen ? <CloseIcon /> : <SearchIcon />}
                </IconButton>
              </Box>
            </Toolbar>
          </Container>
        </AppBar>
      </HideOnScroll>

      {/* Search Bar */}
      {searchOpen && (
        <Box
          sx={{
            width: '100%',
            bgcolor: 'background.paper',
            position: 'absolute',
            top: isMobile ? 56 : 64,
            left: 0,
            zIndex: 1100,
            boxShadow: 3,
            p: 2,
          }}
        >
          <SearchBar onSearch={() => setSearchOpen(false)} />
        </Box>
      )}

      {/* Mobile Menu Drawer */}
      <Drawer
        anchor="left"
        open={mobileMenuOpen}
        onClose={handleMobileMenuToggle}
        sx={{
          '& .MuiDrawer-paper': {
            width: '80%',
            maxWidth: 300,
            bgcolor: 'background.paper',
          },
        }}
      >
        <Box sx={{ p: 2, display: 'flex', alignItems: 'center', justifyContent: 'space-between' }}>
          <Typography variant="h6" component={RouterLink} to="/" sx={{ color: 'text.primary', textDecoration: 'none' }}>
            <MovieIcon sx={{ mr: 1, verticalAlign: 'middle' }} />
            PHIM HAY
          </Typography>
          <IconButton onClick={handleMobileMenuToggle}>
            <CloseIcon />
          </IconButton>
        </Box>
        <Divider />
        <List>
          {/* Loại phim */}
          <ListItem disablePadding>
            <ListItemButton onClick={() => navigate('/')}>
              <ListItemText primary="Trang chủ" />
            </ListItemButton>
          </ListItem>

          {MOVIE_TYPES.map((type) => (
            <ListItem key={type.slug} disablePadding>
              <ListItemButton onClick={() => handleMenuItemClick(`/danh-sach/${type.slug}`)}>
                <ListItemText primary={type.name} />
              </ListItemButton>
            </ListItem>
          ))}

          <Divider sx={{ my: 1 }} />

          {/* Thể loại */}
          <ListItem>
            <Typography variant="subtitle1" sx={{ fontWeight: 'bold' }}>
              Thể loại
            </Typography>
          </ListItem>

          {categories.slice(0, 10).map((category) => (
            <ListItem key={category.slug} disablePadding>
              <ListItemButton onClick={() => handleMenuItemClick(`/the-loai/${category.slug}`)}>
                <ListItemText primary={category.name} />
              </ListItemButton>
            </ListItem>
          ))}

          <Divider sx={{ my: 1 }} />

          {/* Quốc gia */}
          <ListItem>
            <Typography variant="subtitle1" sx={{ fontWeight: 'bold' }}>
              Quốc gia
            </Typography>
          </ListItem>

          {countries.slice(0, 10).map((country) => (
            <ListItem key={country.slug} disablePadding>
              <ListItemButton onClick={() => handleMenuItemClick(`/quoc-gia/${country.slug}`)}>
                <ListItemText primary={country.name} />
              </ListItemButton>
            </ListItem>
          ))}
        </List>
      </Drawer>
    </>
  );
};

export default Header;
