import React, { useState, useEffect, useCallback } from 'react';
import { useParams } from 'react-router-dom';
import { Container, Box, Tabs, Tab } from '@mui/material';
import axios from 'axios';

import MovieInfo from '../components/MovieInfo';
import VideoPlayer from '../components/VideoPlayer';
import EpisodeSelector from '../components/EpisodeSelector';
import RelatedMovies from '../components/RelatedMovies';
import CommentSection from '../components/CommentSection';
import LoadingIndicator from '../components/LoadingIndicator';
import ErrorDisplay from '../components/ErrorDisplay';

const MovieDetail = () => {
  const { slug } = useParams();
  const [movie, setMovie] = useState(null);
  const [currentTab, setCurrentTab] = useState(0);
  const [currentEpisode, setCurrentEpisode] = useState(null);
  const [currentServer, setCurrentServer] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [relatedMovies, setRelatedMovies] = useState([]);

  // Lấy phim liên quan
  const fetchRelatedMovies = useCallback(async (category) => {
    try {
      const response = await axios.get(
        `https://phimapi.com/v1/api/the-loai/${category}?page=1&sort_field=modified.time&sort_type=desc&limit=6`
      );
      if (response.data && response.data.items) {
        // Lọc bỏ phim hiện tại khỏi danh sách phim liên quan
        const filtered = response.data.items.filter(item => item.slug !== slug);
        setRelatedMovies(filtered.slice(0, 6));
      }
    } catch (err) {
      console.error('Error fetching related movies:', err);
    }
  }, [slug]);

  // Lấy thông tin phim từ API
  useEffect(() => {
    const fetchMovieDetail = async () => {
      try {
        setLoading(true);
        setError(null);

        const response = await axios.get(`https://phimapi.com/phim/${slug}`);
        if (response.data) {
          setMovie(response.data);

          // Thiết lập tập phim mặc định
          if (response.data.episodes && Object.keys(response.data.episodes).length > 0) {
            const firstServer = Object.keys(response.data.episodes)[0];
            const firstEpisode = response.data.episodes[firstServer][0];
            const episodeId = typeof firstEpisode === 'object' ? firstEpisode.slug || firstEpisode.id : firstEpisode;

            setCurrentServer(firstServer);
            setCurrentEpisode(episodeId);
          }

          // Lấy phim liên quan
          if (response.data.category && response.data.category.length > 0) {
            const category = response.data.category[0].slug;
            fetchRelatedMovies(category);
          }
        }
        setLoading(false);
      } catch (err) {
        console.error('Error fetching movie details:', err);
        setError('Đã xảy ra lỗi khi tải thông tin phim. Vui lòng thử lại sau.');
        setLoading(false);
      }
    };

    fetchMovieDetail();
  }, [slug, fetchRelatedMovies]);

  // Xử lý khi chọn tập phim
  const handleEpisodeSelect = (episodeId, server) => {
    setCurrentEpisode(episodeId);
    setCurrentServer(server);

    // Cuộn lên đầu trang khi chọn tập mới
    window.scrollTo({ top: 0, behavior: 'smooth' });
  };

  // Xử lý khi chuyển tab
  const handleTabChange = (event, newValue) => {
    setCurrentTab(newValue);
  };

  if (loading) {
    return <LoadingIndicator message="Đang tải thông tin phim..." />;
  }

  if (error) {
    return <ErrorDisplay message={error} onRetry={() => window.location.reload()} />;
  }

  if (!movie) {
    return <ErrorDisplay message="Không tìm thấy thông tin phim." />;
  }

  // Xác định URL video
  const videoUrl = currentEpisode && currentServer && movie.episodes && movie.episodes[currentServer]
    ? movie.episodes[currentServer].find(ep => {
        const epId = typeof ep === 'object' ? ep.slug || ep.id : ep;
        return epId === currentEpisode;
      })
    : null;

  // Xác định tên tập phim hiện tại
  const currentEpisodeName = currentEpisode && currentServer && movie.episodes && movie.episodes[currentServer]
    ? movie.episodes[currentServer].find(ep => {
        const epId = typeof ep === 'object' ? ep.slug || ep.id : ep;
        return epId === currentEpisode;
      })
    : null;

  const episodeTitle = typeof currentEpisodeName === 'object' && currentEpisodeName.name
    ? currentEpisodeName.name
    : `Tập ${currentEpisode}`;

  return (
    <Container maxWidth="xl" sx={{ py: 4 }}>
      {/* Video Player */}
      {videoUrl && movie && (
        <Box sx={{ mb: 4 }}>
          <VideoPlayer
            src={typeof videoUrl === 'object' ? videoUrl.link : videoUrl}
            title={movie && movie.name ? `${movie.name} - ${episodeTitle}` : episodeTitle}
          />
        </Box>
      )}

      {/* Episode Selector */}
      {movie && movie.episodes && Object.keys(movie.episodes).length > 0 && (
        <EpisodeSelector
          episodes={movie.episodes}
          currentEpisode={currentEpisode}
          onEpisodeSelect={handleEpisodeSelect}
        />
      )}

      {/* Tabs */}
      <Box sx={{ borderBottom: 1, borderColor: 'divider', mb: 3 }}>
        <Tabs
          value={currentTab}
          onChange={handleTabChange}
          variant="scrollable"
          scrollButtons="auto"
          allowScrollButtonsMobile
          aria-label="movie detail tabs"
        >
          <Tab label="Thông tin phim" />
          <Tab label="Bình luận" />
        </Tabs>
      </Box>

      {/* Tab Content */}
      <Box role="tabpanel" hidden={currentTab !== 0}>
        {currentTab === 0 && (
          <>
            <MovieInfo movie={movie} />
            <RelatedMovies movies={relatedMovies} />
          </>
        )}
      </Box>

      <Box role="tabpanel" hidden={currentTab !== 1}>
        {currentTab === 1 && movie && movie._id && <CommentSection movieId={movie._id} />}
      </Box>
    </Container>
  );
};

export default MovieDetail;
