import React from 'react';
import { Link as RouterLink } from 'react-router-dom';
import {
  Box,
  Container,
  Grid,
  Typography,
  Link,
  Divider,
  IconButton,
  useMediaQuery,
} from '@mui/material';
import FacebookIcon from '@mui/icons-material/Facebook';
import TwitterIcon from '@mui/icons-material/Twitter';
import InstagramIcon from '@mui/icons-material/Instagram';
import YouTubeIcon from '@mui/icons-material/YouTube';
import MovieIcon from '@mui/icons-material/Movie';

import { useAppContext } from '../context/AppContext';

const Footer = () => {
  const { categories, countries } = useAppContext();
  const isMobile = useMediaQuery((theme) => theme.breakpoints.down('sm'));

  // T·∫°o danh s√°ch nƒÉm t·ª´ nƒÉm hi·ªán t·∫°i ƒë·∫øn 5 nƒÉm tr∆∞·ªõc
  const currentYear = new Date().getFullYear();
  const years = Array.from({ length: 6 }, (_, i) => currentYear - i);

  return (
    <Box
      component="footer"
      sx={{
        py: 5,
        px: 2,
        mt: 'auto',
        background: 'linear-gradient(135deg, #1a1a2e 0%, #16213e 50%, #0f3460 100%)',
        color: 'white',
        borderTop: '1px solid rgba(255,255,255,0.1)'
      }}
    >
      <Container maxWidth="lg">
        <Grid container spacing={4}>
          <Grid item xs={12} sm={4}>
            <Typography 
              variant="h6" 
              gutterBottom 
              sx={{ 
                fontWeight: 'bold',
                position: 'relative',
                display: 'inline-block',
                mb: 3,
                '&:after': {
                  content: '""',
                  position: 'absolute',
                  width: '40px',
                  height: '3px',
                  background: 'linear-gradient(45deg, #ff6b6b, #4ecdc4)',
                  bottom: '-8px',
                  left: 0
                }
              }}
            >
              V·ªÄ CH√öNG T√îI
            </Typography>
            <Box sx={{ display: 'flex', alignItems: 'center', mb: 2 }}>
              <Typography 
                variant="h5" 
                sx={{ 
                  fontWeight: 800,
                  background: 'linear-gradient(45deg, #ff6b6b, #4ecdc4)',
                  backgroundClip: 'text',
                  WebkitBackgroundClip: 'text',
                  WebkitTextFillColor: 'transparent',
                  mr: 1
                }}
              >
                üé¨ PhimHay
              </Typography>
            </Box>
            <Typography variant="body2" sx={{ color: 'rgba(255,255,255,0.7)', lineHeight: 1.7 }}>
              PhimHay cung c·∫•p ƒëa d·∫°ng c√°c b·ªô phim hay v√† m·ªõi nh·∫•t v·ªõi ch·∫•t l∆∞·ª£ng cao,
              ph·ª• ƒë·ªÅ Vi·ªát ngay khi ph√°t h√†nh.
            </Typography>
          </Grid>
          <Grid item xs={12} sm={4}>
            <Typography 
              variant="h6" 
              gutterBottom 
              sx={{ 
                fontWeight: 'bold',
                position: 'relative',
                display: 'inline-block',
                mb: 3,
                '&:after': {
                  content: '""',
                  position: 'absolute',
                  width: '40px',
                  height: '3px',
                  background: 'linear-gradient(45deg, #ff6b6b, #4ecdc4)',
                  bottom: '-8px',
                  left: 0
                }
              }}
            >
              LI√äN K·∫æT
            </Typography>
            <Link 
              href="/" 
              sx={{ 
                display: 'block', 
                mb: 1.5, 
                color: 'rgba(255,255,255,0.7)',
                textDecoration: 'none',
                transition: 'all 0.3s ease',
                '&:hover': {
                  color: '#4ecdc4',
                  transform: 'translateX(5px)'
                }
              }}
            >
              ‚ñ∂ Trang ch·ªß
            </Link>
            <Link 
              href="/danh-sach/phim-moi" 
              sx={{ 
                display: 'block', 
                mb: 1.5, 
                color: 'rgba(255,255,255,0.7)',
                textDecoration: 'none',
                transition: 'all 0.3s ease',
                '&:hover': {
                  color: '#4ecdc4',
                  transform: 'translateX(5px)'
                }
              }}
            >
              ‚ñ∂ Phim m·ªõi
            </Link>
            <Link 
              href="/danh-sach/phim-le" 
              sx={{ 
                display: 'block', 
                mb: 1.5, 
                color: 'rgba(255,255,255,0.7)',
                textDecoration: 'none',
                transition: 'all 0.3s ease',
                '&:hover': {
                  color: '#4ecdc4',
                  transform: 'translateX(5px)'
                }
              }}
            >
              ‚ñ∂ Phim l·∫ª
            </Link>
            <Link 
              href="/danh-sach/phim-bo" 
              sx={{ 
                display: 'block', 
                mb: 1.5, 
                color: 'rgba(255,255,255,0.7)',
                textDecoration: 'none',
                transition: 'all 0.3s ease',
                '&:hover': {
                  color: '#4ecdc4',
                  transform: 'translateX(5px)'
                }
              }}
            >
              ‚ñ∂ Phim b·ªô
            </Link>
            <Link 
              href="/danh-sach/phim-hoat-hinh" 
              sx={{ 
                display: 'block', 
                color: 'rgba(255,255,255,0.7)',
                textDecoration: 'none',
                transition: 'all 0.3s ease',
                '&:hover': {
                  color: '#4ecdc4',
                  transform: 'translateX(5px)'
                }
              }}
            >
              ‚ñ∂ Phim ho·∫°t h√¨nh
            </Link>
          </Grid>
          <Grid item xs={12} sm={4}>
            <Typography 
              variant="h6" 
              gutterBottom 
              sx={{ 
                fontWeight: 'bold',
                position: 'relative',
                display: 'inline-block',
                mb: 3,
                '&:after': {
                  content: '""',
                  position: 'absolute',
                  width: '40px',
                  height: '3px',
                  background: 'linear-gradient(45deg, #ff6b6b, #4ecdc4)',
                  bottom: '-8px',
                  left: 0
                }
              }}
            >
              NƒÇM PH√ÅT H√ÄNH
            </Typography>
            <Box sx={{ display: 'flex', flexWrap: 'wrap', gap: 1 }}>
              {years.map((year) => (
                <Link 
                  key={year} 
                  href={`/nam/${year}`} 
                  sx={{ 
                    display: 'inline-block',
                    minWidth: '40px',
                    textAlign: 'center',
                    py: 0.5,
                    px: 1,
                    borderRadius: '4px',
                    color: 'rgba(255,255,255,0.7)',
                    textDecoration: 'none',
                    transition: 'all 0.3s ease',
                    '&:hover': {
                      background: 'linear-gradient(45deg, #ff6b6b, #4ecdc4)',
                      color: 'white',
                      transform: 'translateY(-3px)'
                    }
                  }}
                >
                  {year}
                </Link>
              ))}
            </Box>
          </Grid>
        </Grid>
        <Box mt={5}>
          <Divider sx={{ borderColor: 'rgba(255,255,255,0.1)' }} />
          <Box mt={3} display="flex" justifyContent="space-between" alignItems="center">
            <Typography variant="body2" sx={{ color: 'rgba(255,255,255,0.5)' }}>
              ¬© {new Date().getFullYear()} PhimHay. T·∫•t c·∫£ quy·ªÅn ƒë∆∞·ª£c b·∫£o l∆∞u.
            </Typography>
            <Box>
              <IconButton 
                sx={{ 
                  color: '#4267B2',
                  bgcolor: 'rgba(255,255,255,0.1)',
                  mr: 1,
                  transition: 'all 0.3s ease',
                  '&:hover': {
                    bgcolor: 'rgba(255,255,255,0.2)',
                    transform: 'translateY(-3px)'
                  }
                }}
                aria-label="Facebook"
              >
                <FacebookIcon />
              </IconButton>
              <IconButton 
                sx={{ 
                  color: '#1DA1F2',
                  bgcolor: 'rgba(255,255,255,0.1)',
                  mr: 1,
                  transition: 'all 0.3s ease',
                  '&:hover': {
                    bgcolor: 'rgba(255,255,255,0.2)',
                    transform: 'translateY(-3px)'
                  }
                }}
                aria-label="Twitter"
              >
                <TwitterIcon />
              </IconButton>
              <IconButton 
                sx={{ 
                  color: '#E1306C',
                  bgcolor: 'rgba(255,255,255,0.1)',
                  transition: 'all 0.3s ease',
                  '&:hover': {
                    bgcolor: 'rgba(255,255,255,0.2)',
                    transform: 'translateY(-3px)'
                  }
                }}
                aria-label="Instagram"
              >
                <InstagramIcon />
              </IconButton>
            </Box>
          </Box>
        </Box>
      </Container>
    </Box>
  );
};

export default Footer;
