import React from 'react';
import { Link as RouterLink } from 'react-router-dom';
import {
  Card,
  CardContent,
  CardMedia,
  Typography,
  Box,
  Chip,
  Rating,
  IconButton,
} from '@mui/material';
import { PlayArrow } from '@mui/icons-material';

const MovieCard = ({ movie }) => {
  // Xử lý khi không có poster
  const posterUrl = movie.poster_url && movie.poster_url.startsWith('http')
    ? movie.poster_url
    : movie.poster_url
      ? `https://phimimg.com/${movie.poster_url}`
      : 'https://via.placeholder.com/300x450?text=No+Image';

  // Xử lý rating từ TMDB nếu có
  const rating = movie.tmdb && movie.tmdb.vote_average ? movie.tmdb.vote_average / 2 : 0;

  return (
    <Card
      sx={{
        height: '100%',
        display: 'flex',
        flexDirection: 'column',
        borderRadius: 3,
        overflow: 'hidden',
        transition: 'all 0.4s cubic-bezier(0.25, 0.46, 0.45, 0.94)',
        background: 'linear-gradient(145deg, #ffffff 0%, #f8f9fa 100%)',
        border: '1px solid rgba(0,0,0,0.05)',
        '&:hover': {
          transform: 'translateY(-12px) scale(1.02)',
          boxShadow: '0 20px 40px rgba(0,0,0,0.2)',
          '& .movie-poster': {
            transform: 'scale(1.1)'
          },
          '& .play-overlay': {
            opacity: 1
          }
        },
      }}
    >
      <Box sx={{ position: 'relative', overflow: 'hidden' }}>
        <CardMedia
          component="img"
          height="320"
          image={posterUrl}
          alt={movie.name}
          className="movie-poster"
          sx={{
            objectFit: 'cover',
            transition: 'transform 0.4s ease',
          }}
        />

        {/* Gradient overlay */}
        <Box
          sx={{
            position: 'absolute',
            top: 0,
            left: 0,
            right: 0,
            bottom: 0,
            background: 'linear-gradient(180deg, rgba(0,0,0,0.1) 0%, rgba(0,0,0,0.3) 100%)',
          }}
        />

        {/* Quality badge */}
        {movie.quality && (
          <Chip
            label={movie.quality}
            size="small"
            sx={{
              position: 'absolute',
              top: 12,
              left: 12,
              fontWeight: 'bold',
              fontSize: '0.75rem',
              background: 'linear-gradient(45deg, #ff6b6b, #ee5a24)',
              color: 'white',
              border: 'none',
              boxShadow: '0 2px 8px rgba(255, 107, 107, 0.4)'
            }}
          />
        )}

        {/* Episode badge */}
        {movie.episode_current && (
          <Chip
            label={movie.episode_current}
            size="small"
            sx={{
              position: 'absolute',
              top: 12,
              right: 12,
              fontWeight: 'bold',
              fontSize: '0.75rem',
              background: 'linear-gradient(45deg, #4ecdc4, #44a08d)',
              color: 'white',
              border: 'none',
              boxShadow: '0 2px 8px rgba(78, 205, 196, 0.4)'
            }}
          />
        )}

        {/* Play overlay */}
        <Box
          className="play-overlay"
          sx={{
            position: 'absolute',
            top: 0,
            left: 0,
            right: 0,
            bottom: 0,
            background: 'rgba(0,0,0,0.7)',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            opacity: 0,
            transition: 'opacity 0.3s ease',
          }}
        >
          <IconButton
             component={RouterLink}
             to={`/phim/${movie.slug}`}
             sx={{
               color: 'white',
               background: 'linear-gradient(45deg, #ff6b6b, #ee5a24)',
               width: 60,
               height: 60,
               '&:hover': {
                 background: 'linear-gradient(45deg, #ee5a24, #ff6b6b)',
                 transform: 'scale(1.1)'
               },
               transition: 'all 0.3s ease'
             }}
           >
             <PlayArrow sx={{ fontSize: 32 }} />
           </IconButton>
        </Box>
      </Box>

      <CardContent sx={{ flexGrow: 1, p: 3 }}>
        <Typography
          variant="h6"
          component={RouterLink}
          to={`/phim/${movie.slug}`}
          sx={{
            fontWeight: 'bold',
            fontSize: '1.1rem',
            lineHeight: 1.3,
            display: '-webkit-box',
            WebkitLineClamp: 2,
            WebkitBoxOrient: 'vertical',
            overflow: 'hidden',
            textOverflow: 'ellipsis',
            textDecoration: 'none',
            color: 'text.primary',
            mb: 1.5,
            '&:hover': {
              background: 'linear-gradient(45deg, #667eea, #764ba2)',
              backgroundClip: 'text',
              WebkitBackgroundClip: 'text',
              WebkitTextFillColor: 'transparent',
            },
            transition: 'all 0.3s ease'
          }}
        >
          {movie.name}
        </Typography>

        {movie.origin_name && (
          <Typography
            variant="body2"
            color="text.secondary"
            sx={{
              fontSize: '0.9rem',
              mb: 2,
              display: '-webkit-box',
              WebkitLineClamp: 1,
              WebkitBoxOrient: 'vertical',
              overflow: 'hidden',
              textOverflow: 'ellipsis',
              fontStyle: 'italic'
            }}
          >
            {movie.origin_name}
          </Typography>
        )}

        <Box sx={{ 
          display: 'flex', 
          justifyContent: 'space-between', 
          alignItems: 'center',
          pt: 1,
          borderTop: '1px solid rgba(0,0,0,0.05)'
        }}>
          {movie.year && (
            <Typography 
              variant="body2" 
              sx={{
                color: '#667eea',
                fontWeight: 'bold',
                fontSize: '0.9rem'
              }}
            >
              📅 {movie.year}
            </Typography>
          )}

          {/* Rating */}
          {rating > 0 && (
            <Box sx={{ display: 'flex', alignItems: 'center' }}>
              <Rating
                value={rating}
                precision={0.5}
                readOnly
                size="small"
                sx={{ 
                  mr: 0.5,
                  '& .MuiRating-iconFilled': {
                    color: '#ffd700'
                  }
                }}
              />
              <Typography 
                variant="caption" 
                sx={{
                  color: '#ffd700',
                  fontWeight: 'bold'
                }}
              >
                {rating.toFixed(1)}
              </Typography>
            </Box>
          )}
        </Box>
      </CardContent>
    </Card>
  );
};

export default MovieCard;
