import React, { useState, useEffect, useRef } from 'react';
import { Box, Typography, IconButton, Slider, Paper, Grid } from '@mui/material';
import {
  PlayArrow,
  Pause,
  VolumeUp,
  VolumeOff,
  Fullscreen,
  SettingsOutlined,
  Forward10,
  Replay10,
} from '@mui/icons-material';

const VideoPlayer = ({ src, title }) => {
  const videoRef = useRef(null);
  const containerRef = useRef(null);
  const [isPlaying, setIsPlaying] = useState(false);
  const [currentTime, setCurrentTime] = useState(0);
  const [duration, setDuration] = useState(0);
  const [volume, setVolume] = useState(1);
  const [isMuted, setIsMuted] = useState(false);
  const [showControls, setShowControls] = useState(true);
  const [isFullscreen, setIsFullscreen] = useState(false);
  const controlsTimeoutRef = useRef(null);

  // Xử lý khi video được tải
  useEffect(() => {
    const video = videoRef.current;
    if (!video) return;

    const handleLoadedMetadata = () => {
      setDuration(video.duration);
    };

    const handleTimeUpdate = () => {
      setCurrentTime(video.currentTime);
    };

    const handleEnded = () => {
      setIsPlaying(false);
    };

    video.addEventListener('loadedmetadata', handleLoadedMetadata);
    video.addEventListener('timeupdate', handleTimeUpdate);
    video.addEventListener('ended', handleEnded);

    return () => {
      video.removeEventListener('loadedmetadata', handleLoadedMetadata);
      video.removeEventListener('timeupdate', handleTimeUpdate);
      video.removeEventListener('ended', handleEnded);
    };
  }, []);

  // Xử lý hiển thị/ẩn controls
  useEffect(() => {
    const handleMouseMove = () => {
      setShowControls(true);
      clearTimeout(controlsTimeoutRef.current);
      controlsTimeoutRef.current = setTimeout(() => {
        if (isPlaying) {
          setShowControls(false);
        }
      }, 3000);
    };

    const container = containerRef.current;
    if (container) {
      container.addEventListener('mousemove', handleMouseMove);
      container.addEventListener('mouseenter', handleMouseMove);
      container.addEventListener('mouseleave', () => {
        if (isPlaying) {
          setShowControls(false);
        }
      });
    }

    return () => {
      if (container) {
        container.removeEventListener('mousemove', handleMouseMove);
        container.removeEventListener('mouseenter', handleMouseMove);
        container.removeEventListener('mouseleave', () => {});
      }
      clearTimeout(controlsTimeoutRef.current);
    };
  }, [isPlaying]);

  // Xử lý phát/dừng video
  const togglePlay = () => {
    const video = videoRef.current;
    if (!video) return;

    if (isPlaying) {
      video.pause();
    } else {
      video.play();
    }
    setIsPlaying(!isPlaying);
  };

  // Xử lý thay đổi thời gian video
  const handleTimeChange = (_, newValue) => {
    const video = videoRef.current;
    if (!video) return;

    video.currentTime = newValue;
    setCurrentTime(newValue);
  };

  // Xử lý thay đổi âm lượng
  const handleVolumeChange = (_, newValue) => {
    const video = videoRef.current;
    if (!video) return;

    video.volume = newValue;
    setVolume(newValue);
    setIsMuted(newValue === 0);
  };

  // Xử lý tắt/bật âm thanh
  const toggleMute = () => {
    const video = videoRef.current;
    if (!video) return;

    const newMutedState = !isMuted;
    video.muted = newMutedState;
    setIsMuted(newMutedState);
    if (newMutedState) {
      video.volume = 0;
      setVolume(0);
    } else {
      video.volume = volume > 0 ? volume : 0.5;
      setVolume(volume > 0 ? volume : 0.5);
    }
  };

  // Xử lý chế độ toàn màn hình
  const toggleFullscreen = () => {
    const container = containerRef.current;
    if (!container) return;

    if (!document.fullscreenElement) {
      container.requestFullscreen().catch((err) => {
        console.error(`Error attempting to enable full-screen mode: ${err.message}`);
      });
    } else {
      document.exitFullscreen();
    }
  };

  // Xử lý sự kiện fullscreen change
  useEffect(() => {
    const handleFullscreenChange = () => {
      setIsFullscreen(!!document.fullscreenElement);
    };

    document.addEventListener('fullscreenchange', handleFullscreenChange);
    return () => {
      document.removeEventListener('fullscreenchange', handleFullscreenChange);
    };
  }, []);

  // Xử lý tua nhanh/lùi 10 giây
  const handleForward = () => {
    const video = videoRef.current;
    if (!video) return;
    video.currentTime = Math.min(video.currentTime + 10, video.duration);
  };

  const handleRewind = () => {
    const video = videoRef.current;
    if (!video) return;
    video.currentTime = Math.max(video.currentTime - 10, 0);
  };

  // Format thời gian
  const formatTime = (timeInSeconds) => {
    const hours = Math.floor(timeInSeconds / 3600);
    const minutes = Math.floor((timeInSeconds % 3600) / 60);
    const seconds = Math.floor(timeInSeconds % 60);

    if (hours > 0) {
      return `${hours}:${minutes.toString().padStart(2, '0')}:${seconds.toString().padStart(2, '0')}`;
    }
    return `${minutes}:${seconds.toString().padStart(2, '0')}`;
  };

  return (
    <Box
      ref={containerRef}
      sx={{
        position: 'relative',
        width: '100%',
        height: 0,
        paddingBottom: '56.25%', // 16:9 aspect ratio
        backgroundColor: '#000',
        borderRadius: 1,
        overflow: 'hidden',
        '&:hover': {
          '& .video-controls': {
            opacity: 1,
          },
        },
      }}
    >
      <video
        ref={videoRef}
        src={src}
        style={{
          position: 'absolute',
          top: 0,
          left: 0,
          width: '100%',
          height: '100%',
          objectFit: 'contain',
        }}
        onClick={togglePlay}
      />

      {/* Video Title */}
      {title && showControls && (
        <Box
          sx={{
            position: 'absolute',
            top: 0,
            left: 0,
            right: 0,
            padding: 2,
            background: 'linear-gradient(to bottom, rgba(0,0,0,0.7) 0%, rgba(0,0,0,0) 100%)',
            color: 'white',
            transition: 'opacity 0.3s',
            opacity: showControls ? 1 : 0,
          }}
        >
          <Typography variant="h6">{title}</Typography>
        </Box>
      )}

      {/* Video Controls */}
      <Box
        className="video-controls"
        sx={{
          position: 'absolute',
          bottom: 0,
          left: 0,
          right: 0,
          padding: 2,
          background: 'linear-gradient(to top, rgba(0,0,0,0.7) 0%, rgba(0,0,0,0) 100%)',
          color: 'white',
          transition: 'opacity 0.3s',
          opacity: showControls ? 1 : 0,
          display: 'flex',
          flexDirection: 'column',
        }}
      >
        {/* Progress Bar */}
        <Slider
          value={currentTime}
          max={duration || 100}
          onChange={handleTimeChange}
          sx={{
            color: 'primary.main',
            height: 4,
            '& .MuiSlider-thumb': {
              width: 12,
              height: 12,
              transition: '0.3s cubic-bezier(.47,1.64,.41,.8)',
              '&:hover, &.Mui-focusVisible': {
                boxShadow: '0px 0px 0px 8px rgba(25, 118, 210, 0.16)',
              },
              '&.Mui-active': {
                width: 16,
                height: 16,
              },
            },
            '& .MuiSlider-rail': {
              opacity: 0.28,
            },
          }}
        />

        {/* Controls */}
        <Grid container alignItems="center" spacing={1}>
          <Grid item>
            <IconButton onClick={togglePlay} color="inherit" size="small">
              {isPlaying ? <Pause /> : <PlayArrow />}
            </IconButton>
          </Grid>

          <Grid item>
            <IconButton onClick={handleRewind} color="inherit" size="small">
              <Replay10 />
            </IconButton>
          </Grid>

          <Grid item>
            <IconButton onClick={handleForward} color="inherit" size="small">
              <Forward10 />
            </IconButton>
          </Grid>

          <Grid item>
            <Box sx={{ display: 'flex', alignItems: 'center', width: 100 }}>
              <IconButton onClick={toggleMute} color="inherit" size="small">
                {isMuted ? <VolumeOff /> : <VolumeUp />}
              </IconButton>
              <Slider
                size="small"
                value={volume}
                max={1}
                step={0.01}
                onChange={handleVolumeChange}
                sx={{ ml: 1, color: 'white' }}
              />
            </Box>
          </Grid>

          <Grid item xs>
            <Typography variant="body2" sx={{ textAlign: 'center' }}>
              {formatTime(currentTime)} / {formatTime(duration)}
            </Typography>
          </Grid>

          <Grid item>
            <IconButton color="inherit" size="small">
              <SettingsOutlined />
            </IconButton>
          </Grid>

          <Grid item>
            <IconButton onClick={toggleFullscreen} color="inherit" size="small">
              <Fullscreen />
            </IconButton>
          </Grid>
        </Grid>
      </Box>

      {/* Play/Pause Overlay */}
      {!isPlaying && (
        <Box
          sx={{
            position: 'absolute',
            top: '50%',
            left: '50%',
            transform: 'translate(-50%, -50%)',
            backgroundColor: 'rgba(0, 0, 0, 0.5)',
            borderRadius: '50%',
            width: 80,
            height: 80,
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            cursor: 'pointer',
          }}
          onClick={togglePlay}
        >
          <PlayArrow sx={{ fontSize: 50, color: 'white' }} />
        </Box>
      )}
    </Box>
  );
};

export default VideoPlayer;
