import React from 'react';
import Slider from 'react-slick';
import { Box, Typography, useTheme, useMediaQuery } from '@mui/material';
import MovieCard from './MovieCard';
import 'slick-carousel/slick/slick.css';
import 'slick-carousel/slick/slick-theme.css';

const MovieSlider = ({ title, movies, slidesToShow = 6 }) => {
  const theme = useTheme();
  const isXs = useMediaQuery(theme.breakpoints.down('sm'));
  const isSm = useMediaQuery(theme.breakpoints.between('sm', 'md'));
  const isMd = useMediaQuery(theme.breakpoints.between('md', 'lg'));
  const isLg = useMediaQuery(theme.breakpoints.between('lg', 'xl'));

  // Responsive settings
  const getResponsiveSlidesToShow = () => {
    if (isXs) return 2;
    if (isSm) return 3;
    if (isMd) return 4;
    if (isLg) return 5;
    return slidesToShow;
  };

  const settings = {
    dots: false,
    infinite: movies.length > getResponsiveSlidesToShow(),
    speed: 500,
    slidesToShow: getResponsiveSlidesToShow(),
    slidesToScroll: 1,
    autoplay: true,
    autoplaySpeed: 5000,
    pauseOnHover: true,
    swipeToSlide: true,
    arrows: !isXs,
    responsive: [
      {
        breakpoint: 1536, // xl
        settings: {
          slidesToShow: 5,
          slidesToScroll: 1,
        },
      },
      {
        breakpoint: 1200, // lg
        settings: {
          slidesToShow: 4,
          slidesToScroll: 1,
        },
      },
      {
        breakpoint: 900, // md
        settings: {
          slidesToShow: 3,
          slidesToScroll: 1,
        },
      },
      {
        breakpoint: 600, // sm
        settings: {
          slidesToShow: 2,
          slidesToScroll: 1,
          arrows: false,
        },
      },
    ],
  };

  if (!movies || movies.length === 0) {
    return null;
  }

  return (
    <Box sx={{ mb: 4 }}>
      <Typography
        variant="h5"
        component="h2"
        sx={{
          mb: 2,
          fontWeight: 'bold',
          display: 'flex',
          alignItems: 'center',
          '&::before': {
            content: '""',
            width: 4,
            height: 24,
            backgroundColor: 'primary.main',
            display: 'inline-block',
            marginRight: 1,
            borderRadius: 1,
          },
        }}
      >
        {title}
      </Typography>

      <Box
        sx={{
          '.slick-prev, .slick-next': {
            zIndex: 1,
            width: 40,
            height: 40,
            backgroundColor: 'rgba(0, 0, 0, 0.5)',
            borderRadius: '50%',
            transition: 'all 0.3s ease',
            '&:hover': {
              backgroundColor: 'primary.main',
            },
            '&::before': {
              fontSize: 20,
            },
          },
          '.slick-prev': {
            left: -5,
          },
          '.slick-next': {
            right: -5,
          },
          '.slick-track': {
            display: 'flex',
            gap: 2,
            margin: '0 -8px',
          },
          '.slick-slide': {
            padding: '0 8px',
            height: 'auto',
            '& > div': {
              height: '100%',
            },
          },
        }}
      >
        <Slider {...settings}>
          {movies.map((movie) => (
            <Box key={movie._id} sx={{ height: '100%', pb: 1 }}>
              <MovieCard movie={movie} />
            </Box>
          ))}
        </Slider>
      </Box>
    </Box>
  );
};

export default MovieSlider;
