import React from 'react';
import { Pagination as MuiPagination, Box, useTheme, useMediaQuery } from '@mui/material';

const Pagination = ({ currentPage, totalPages, onPageChange, sx }) => {
  const theme = useTheme();
  const isMobile = useMediaQuery(theme.breakpoints.down('sm'));

  // Xác định số lượng nút phân trang hiển thị dựa trên kích thước màn hình
  const siblingCount = isMobile ? 0 : 1;
  const boundaryCount = isMobile ? 1 : 2;

  if (totalPages <= 1) return null;

  return (
    <Box
      sx={{
        display: 'flex',
        justifyContent: 'center',
        my: 4,
        ...sx,
      }}
    >
      <MuiPagination
        count={totalPages}
        page={currentPage}
        onChange={(_, page) => onPageChange(page)}
        color="primary"
        size={isMobile ? 'medium' : 'large'}
        siblingCount={siblingCount}
        boundaryCount={boundaryCount}
        showFirstButton
        showLastButton
        sx={{
          '& .MuiPaginationItem-root': {
            color: 'text.primary',
          },
          '& .MuiPaginationItem-page.Mui-selected': {
            backgroundColor: 'primary.main',
            color: 'white',
            fontWeight: 'bold',
            '&:hover': {
              backgroundColor: 'primary.dark',
            },
          },
        }}
      />
    </Box>
  );
};

export default Pagination;
